import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const Form = () => {
    const [formData, setFormData] = useState({
        name: '',
        age: '',
        surname: '',
        gender: '',
        gay: false
    });

    let globalData = [];

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8080/task02/data', formData)
            .then(response => {
                globalData.push(formData);
                console.log('Data saved successfully:', response.data);
            })
            .catch(error => {
                console.error('There was an error saving the data!', error);
            }).finally(
                console.log(globalData)
            )
    };

    return (
        <form onSubmit={handleSubmit} method='POST'>
            <div  className={"form-group"}>
                <label className={'form-check-label'} >Name:</label>
                <input className={"form-control"} type="text" name="name" value={formData.name} onChange={handleChange} />
            </div>
            <div  className={"form-group"}>
                <label className={'form-check-label'}>Age:</label>
                <input  className={"form-control"}  type="number" name="age" value={formData.age} onChange={handleChange} />
            </div>
            <div  className={"form-group"}>
                <label className={'form-check-label'}>Surname:</label>
                <input  className={"form-control"}  type="text" name="surname" value={formData.surname} onChange={handleChange} />
            </div>
            <div  className={"btn-group btn-group-toggle"}>
                <label className={'btn btn-secondary active'}>Gender:</label>
                <div>
                    <input type="radio" name="gender" value="male" checked={formData.gender === 'male'} onChange={handleChange} /> Male
                </div>
                <div>
                    <input type="radio" name="gender" value="female" checked={formData.gender === 'female'} onChange={handleChange} /> Female
                </div>
                <div>
                    <input type="radio" name="gender" value="other" checked={formData.gender === 'other'} onChange={handleChange} /> Other
                </div>
            <div  className={"form-group"}>
                <input type="checkbox" name="gay" checked={formData.gay || false} onChange={(e) => setFormData({ ...formData, gay: e.target.checked })} /> Gay
            </div>
            </div>
            <button className={'btn btn-primary'} type="submit">Submit</button>
        </form>
    );
};

export default Form;
