import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';


const Form = () => {
    const [formData, setFormData] = useState({
        name: '',
        age: '',
        surname: '',
        gender: '',
        gay: false
    });

    let globalData = [];

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8080/task02/data', formData)
            .then(response => {
                globalData.push(formData);
                console.log('Data saved successfully:', response.data);
            })
            .catch(error => {
                console.error('There was an error saving the data!', error);
            }).finally(
                console.log(globalData)
            )
    };

    return (
        <form onSubmit={handleSubmit} method='POST'>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputName">Name:</label>
                    <input className="form-control" type="text" id="inputName" name="name" value={formData.name} onChange={handleChange} />
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputSurname">Surname:</label>
                    <input className="form-control" type="text" id="inputSurname" name="surname" value={formData.surname} onChange={handleChange} />
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputAge">Age:</label>
                    <input className="form-control" type="number" id="inputAge" name="age" value={formData.age} onChange={handleChange} />
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputGender">Gender:</label>
                    <div className="btn-group btn-group-toggle" data-toggle="buttons">
                        <label className="btn btn-secondary">
                            <input type="radio" name="gender" value="male" checked={formData.gender === 'male'} onChange={handleChange} autoComplete='off' /> Male
                        </label>
                        <label className="btn btn-secondary">
                            <input type="radio" name="gender" value="female" checked={formData.gender === 'female'} onChange={handleChange} autoComplete='off' /> Female
                        </label>
                        <label className="btn btn-secondary">
                            <input type="radio" name="gender" value="other" checked={formData.gender === 'other'} onChange={handleChange} autoComplete='off' /> Other
                        </label>
                    </div>
                </div>
            </div>
            <div className="form-group">
                <div className="form-check">
                    <input className="form-check-input" type="checkbox" id="inputGay" name="gay" checked={formData.gay || false} onChange={(e) => setFormData({ ...formData, gay: e.target.checked })} />
                    <label className="form-check-label" htmlFor="inputGay">Gay</label>
                </div>
            </div>
            <button className="btn btn-primary" type="submit">Submit</button>
        </form>
    );
};

export default Form;
