import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const PokettoKomponento = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedTypes, setSelectedTypes] = useState([]);

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    useEffect(() => {
        axios.get('http://localhost:8080/pokemon/types')
            .then(response => {
                console.log(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the data!', error);
            });
    }, []);


    const handleTypeChange = (e) => {
        const type = e.target.value;
        setSelectedTypes((prevTypes) =>
            prevTypes.includes(type)
                ? prevTypes.filter((t) => t !== type)
                : [...prevTypes, type]
        );
    };

    return (
        <div className="container mt-5">
            <div className="mt-4">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Search Pokemon"
                    value={searchTerm}
                    onChange={handleSearchChange}
                />
            </div>
            <div className="mt-3">
                <h6>Filter by Type:</h6>
                {/* get the values from axios.get from route /pokemon/types */}

            </div>
            <div className="mt-4">
                <h6>Pokemon Details:</h6>
                <div className="card">
                    <div className="card-body">
                        <h5 className="card-title">Name: </h5>
                        <p className="card-text">ID: </p>
                        <p className="card-text">Stats: </p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PokettoKomponento;
