import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const PokettoKomponento = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedTypes, setSelectedTypes] = useState([]);

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleTypeChange = (e) => {
        const type = e.target.value;
        setSelectedTypes((prevTypes) =>
            prevTypes.includes(type)
                ? prevTypes.filter((t) => t !== type)
                : [...prevTypes, type]
        );
    };

    return (
        <div className="container mt-5">
            <div className="mt-4">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Search Pokemon"
                    value={searchTerm}
                    onChange={handleSearchChange}
                />
            </div>
            <div className="mt-3">
                <h6>Filter by Type:</h6>
                {['Fire', 'Water', 'Grass', 'Electric', 'Ice', 'Fighting', 'Poison', 'Ground', 'Flying', 'Psychic', 'Bug', 'Rock', 'Ghost', 'Dragon', 'Dark', 'Steel', 'Fairy'].map((type) => (
                    <div className="form-check form-check-inline" key={type}>
                        <input
                            className="form-check-input"
                            type="checkbox"
                            value={type}
                            id={`type-${type}`}
                            onChange={handleTypeChange}
                        />
                        <label className="form-check-label" htmlFor={`type-${type}`}>
                            {type}
                        </label>
                    </div>
                ))}
            </div>
            <div className="mt-4">
                <h6>Pokemon Details:</h6>
                <div className="card">
                    <div className="card-body">
                        <h5 className="card-title">Name: </h5>
                        <p className="card-text">ID: </p>
                        <p className="card-text">Stats: </p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PokettoKomponento;
