import posts from '../posts.json'
import { useState } from 'react';
import ComponentToDisplay from './ComponentToDisplay';



function DisplayComponents() {

    const [sliderValue, setSliderValue] = useState(0);

    const handleSlide = (event) => {
      setSliderValue(event.target.value)
      console.log(event.target.value)
    }
    return (
        <div className="App"> 
        <input type="range" min="1" max="100" value={sliderValue} onChange={handleSlide} id="myRange"></input>
        {posts.map((element, index, array) => {
          if (element.id < 10){
            element.toString().padStart(2, '0');
            console.log(element)
          }
          return <ComponentToDisplay title={element.title} image={`./icons/Icon14_${element.id}.png`} description={element.description} key={index}/>
        })}



      </div>
    );



}


export default DisplayComponents;
