import React, { useState, useRef } from 'react';

function Form () {

    const [first, setFirst] = useState("");
    const [second, setSecond] = useState("");
    const [dropdown, setDropdown] = useState("");
    const [radio, setRadio] = useState("");
    const ref = useRef();
    function onSelect(color) {
        setRadio(color);
    }

    function handleChangeBackground() {
        // Change background color based on selected radio button value
        if (radio === "Blue") {
            document.body.style.backgroundColor = "blue";
        } else if (radio === "Green") {
            document.body.style.backgroundColor = "green";
        } else if (radio === "Red") {
            document.body.style.backgroundColor = "red";
        } else if (radio === "Pink") {
            document.body.style.backgroundColor = "pink";
        }
    }



    return (
        <form >
            <input type="text"  placeholder="Bla bla bla..." required onChange={(event) => {
                setFirst(event.target.value);
            }}/>

            <br/>

            <input type="text" placeholder="Bla bla bla..."required onChange={(event) => {
                setSecond(event.target.value);
            }} />

            <br/>

            <select onChange={(event) => {
                setDropdown(event.target.value);
                console.log(event.target.value);
            }}>
                <option value="GPU">GPU</option >
                <option value="CPU">CPU</option>
                <option value="RAM">RAM</option>
            </select>

            <br/>

            {/* 4 radio buttons */}



            <input type="radio" name="radbut" value={"Blue"}required onClick={() => {
                onSelect("Blue");
            }}/>
            <label>Blue</label>
            <br/>
            <input type="radio"  name="radbut"  value={"Green"}required onClick={() => {
                onSelect("Green");
            }} />
            <label>Green</label>
            <br/>
            <input type="radio"  name="radbut" radioGroup="group" value={"Red"}required  onClick={() => {
                onSelect("Red");
            }}/>
            <label>Red</label>
            <br/>
            <input type="radio"  name="radbut" radioGroup="group" value={"Pink"} required onClick={() => {
                onSelect("Pink");
            }}/>
            <label>Pink</label>
            <br/>

            <br/>

            <input type="button" onClick={() => {

                console.log(second);
                console.log(dropdown);
                console.log(radio);
                console.log(first);

                handleChangeBackground();
            }}/>
        </form>

    );
}

export default Form;
