function Form () {

    const [first, setFirst] = useState("");
    const [second, setSecond] = useState("");
    const [dropdown, setDropdown] = useState("");
    const [radio, setRadio] = useState("");



    return (
        <form>
            <input type="text"  placeholder="Bla bla bla..." required onChange={(event) => {
                setFirst(event.target.value);
            }}/>

            <br/>

            <input type="text" placeholder="Bla bla bla..."required onChange={(event) => {
                setFirst(event.target.value);
            }} />

            <br/>

            <select onChange={(event) => {
                setDropdown(event.target.value);
            }}>
                <option value="GPU">GPU</option >
                <option value="CPU">CPU</option>
                <option value="RAM">RAM</option>
            </select>

            <br/>

            {/* 4 radio buttons */}
            <input type="radio" name="radbut" value={"Blue"}required/>
            <label>Blue</label>
            <br/>
            <input type="radio"  name="radbut"  value={"Green"}required/>
            <label>Green</label>
            <br/>
            <input type="radio"  name="radbut" radioGroup="group" value={"Red"}required/>
            <label>Red</label>
            <br/>
            <input type="radio"  name="radbut" radioGroup="group" value={"Pink"} required/>
            <label>Pink</label>
            <br/>

            <br/>

            <input type="button" onClick={() => {

                console.log(first);

                if (radio.value === "Blue") {
                    console.log("Blue");
                }
            }}/>
        </form>
    );
}

export default Form;
